import React, { useState } from 'react'
import './Counter.css'

const Counter = () => {

    const handleOnClick = operador => {
        const [contador, setContador] = useState(0)
        setContador
    }

    return (
        <div className='contador'>
            <span>{contador}</span>
            <button onClick={() => handleOnClick('-')}>-</button>
            <button onClick={() => handleOnClick('+')}>+</button>
        </div>
    )
}

export default Counter
