import React, { useState } from 'react'
import './Counter.css'

const Counter = () => {
    const [count, setCount] = useState(0)

    const handleOnClick = operador => {
        let novoValor = operador
        setCount(456)
    }

    return (
        <div className='contador'>
            <span>{count}</span>
            <button onClick={() => handleOnClick('-')}>-</button>
            <button onClick={() => handleOnClick('+')}>+</button>
        </div>
    )
}

export default Counter
